public class FindInSorted {
    private FindInSorted() {
        // private constructor to prevent instantiation
    }

    public static int binarySearch(final int[] array, final int target, final int start, final int end) {
        int result = -1;
        int low = start;
        int high = end;

        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (target < array[mid]) {
                high = mid - 1;
            } else if (target > array[mid]) {
                low = mid + 1;
            } else {
                result = mid;
                return result;
            }
        }

        return result;
    }

    public static int findInSorted(final int[] array, final int target) {
        return binarySearch(array, target, 0, array.length - 1);
    }
}

