class GetFactors {
    public static List<Integer> getFactors(int number) {
        if (number == 1) {
            return Collections.emptyList();
        }
        final int max = (int) (Math.sqrt(number) + 1.0);
        for (int i = 2; i < max; i++) {
            if (number % i == 0) {
                List<Integer> factors = new ArrayList<>();
                factors.add(i);
                factors.addAll(getFactors(number / i));
                return factors;
            }
        }
        return Collections.emptyList();
    }
}

