public class KnapsackSolver {
    public static int knapsack(final int capacity, final int[][] items) {
        int itemWeight, itemValue;
        final int totalItems = items.length;
        final int[][] memoizationTable = new int[totalItems + 1][capacity + 1];

        for (int i = 0; i <= totalItems; i++) {
            if (i - 1 >= 0) {
                itemWeight = items[i - 1][0];
                itemValue = items[i - 1][1];
            }
            for (int currentCapacity = 0; currentCapacity <= capacity; currentCapacity++) {
                if (i == 0 || currentCapacity == 0) {
                    memoizationTable[i][currentCapacity] = 0;
                } else if (itemWeight < currentCapacity) {
                    memoizationTable[i][currentCapacity] = Math.max(memoizationTable[i - 1][currentCapacity], itemValue + memoizationTable[i - 1][currentCapacity - itemWeight]);
                } else {
                    memoizationTable[i][currentCapacity] = memoizationTable[i - 1][currentCapacity];
                }
            }
        }
        return memoizationTable[totalItems][capacity];
    }
}

