    public static Integer findKth(List<Integer> numbers, final int k) {
        final int pivot = numbers.get(0);
        final List<Integer> below = new ArrayList<>(); 
        final List<Integer> above = new ArrayList<>();

        for (Integer num : numbers) {
            if (num < pivot) {
                below.add(num);
            } else if (num > pivot) {
                above.add(num);
            }
        }

        final int numLessThanPivot = below.size();
        final int numLessThanOrEqualPivot = numbers.size() - above.size();

        if (k < numLessThanPivot) {
            return findKth(below, k);
        } else if (k >= numLessThanOrEqualPivot) {
            return findKth(above, k);
        } else {
            return pivot;
        }
    }
}

