            if (stringOne.charAt(i) == stringTwo.charAt(j)) {
                int previousLCSLength = dpTable.get(i - 1).get(j - 1);
                dpTable.get(i).put(j, previousLCSLength + 1);
            } else {
                dpTable.get(i).put(j, Math.max(dpTable.get(i - 1).get(j), dpTable.get(i).get(j - 1)));
            }
        }
    }

    return retrieveLCSLength(dpTable, stringOne.length());
}

