package java_programs;
import java.util.*;
public final class LEVENSHTEIN {
    public static int levenshtein(final String source, final String target) {
        return calculateDistance(source, target);        
    }
    
    private static int calculateDistance(final String source, final String target) {
        if (source.isEmpty() || target.isEmpty()) {
            return source.isEmpty() ? target.length() : source.length();
        } 
        
        if (source.charAt(0) == target.charAt(0)) {
            return 1 + levenshtein(source.substring(1), target.substring(1));
        } 
        
        int insertion = levenshtein(source, target.substring(1));
        int substitution = levenshtein(source.substring(1), target.substring(1));
        int deletion = levenshtein(source.substring(1), target);

        return 1 + Math.min(Math.min(insertion, substitution), deletion);
    }
}

