        int longest = 0;

        int i = 0;
        for (int val : arr) {

            ArrayList<Integer> prefix_lengths = new ArrayList<Integer>(100);
            for (int j=1; j < longest+1; j++) {
                if (arr[ends.get(j)] < val) {
                    prefix_lengths.add(j);
                }
