            int length = !prefix_lengths.isEmpty() ? Collections.max(prefix_lengths) : 0;

            if (length == longest || val < arr[ends.get(length+1)]) {
                ends.put(length+1, i);
                longest = length + 1;
            }

            i++;
        }
        return longest;
    }
}

