import java.util.List;
import java.util.ArrayList;

public class MergeSort {

    private MergeSort() {
        // private constructor to prevent instantiation
    }

    public static List<Integer> merge(final List<Integer> left, final List<Integer> right) {
        List<Integer> result = new ArrayList<>();

        int leftIndex = 0;
        int rightIndex = 0;

        while (leftIndex < left.size() && rightIndex < right.size()) {
            if (left.get(leftIndex) <= right.get(rightIndex)) {
                result.add(left.get(leftIndex));
                leftIndex++;
            } else {
                result.add(right.get(rightIndex));
                rightIndex++;
            }
        }

        result.addAll(left.subList(leftIndex, left.size()).isEmpty() ? right.subList(rightIndex, right.size()) : left.subList(leftIndex, left.size()));

        return result;
    }

    public static List<Integer> mergeSort(final List<Integer> arr) {
        if (arr.isEmpty()) {
            return arr;
        } 

        final int middle = arr.size() / 2;
        final List<Integer> left = new ArrayList<>(arr.subList(0, middle));
        final List<Integer> sortedLeft = mergeSort(left);

        final List<Integer> right = new ArrayList<>(arr.subList(middle, arr.size()));
        final List<Integer> sortedRight = mergeSort(right);

        return merge(sortedLeft, sortedRight);
    }
}

