public class MinimumSpanningTree {
    public static Set<WeightedEdge> calculateMinimumSpanningTree(final List<WeightedEdge> weightedEdges) {
        Map<Node, Set<Node>> groupByNode = new HashMap<>();
        Set<WeightedEdge> minSpanningTree = new HashSet<>();

        weightedEdges.sort(null);

        for (WeightedEdge edge : weightedEdges) {
           final Node vertexU = edge.node1;
           final Node vertexV = edge.node2;

           if (!groupByNode.containsKey(vertexU)) {
               groupByNode.put(vertexU, new HashSet<>(Arrays.asList(vertexU)));
           }
           if (!groupByNode.containsKey(vertexV)) {
               groupByNode.put(vertexV, new HashSet<>(Arrays.asList(vertexV)));
           }

            if (!Objects.equals(groupByNode.get(vertexU), groupByNode.get(vertexV))) {
                minSpanningTree.add(edge);
                groupByNode = update(groupByNode, vertexU, vertexV);
                for (final Node node : Objects.requireNonNull(groupByNode.get(vertexV))) {
                    groupByNode = update(groupByNode, node, vertexU);
                }
            }
        }
        return minSpanningTree;
    }

    private static Map<Node, Set<Node>> update(final Map<Node, Set<Node>> groupByNode, final Node vertexU, final Node vertexV) {
        Set<Node> vertexUGroup = Objects.requireNonNullElseGet(groupByNode.get(vertexU), HashSet::new);
        vertexUGroup.addAll(Objects.requireNonNullElseGet(groupByNode.get(vertexV), HashSet::new));
        groupByNode.put(vertexU, vertexUGroup);

        return groupByNode;
    }
}

