                        ArrayList<Integer> reversed = new ArrayList<Integer>(100);
                        for (int k=next_perm.size()-1; k!=i; k--) {
                            reversed.add(next_perm.get(k));
                        }

                        int q = i + 1;
                        for (Integer replace : reversed) {
                            next_perm.set(q, replace);
                            q++;
                        }

                        return next_perm;
                    }
                }
            }
        }

        return new ArrayList<Integer>();
