import java.util.ArrayList;
import java.util.Collections;
import java.util.List;

public class NextPermutation {

    public static List<Integer> nextPermutation(List<Integer> permutation) {
        for (int index = permutation.size() - 2; index >= 0; index--) {
            if (permutation.get(index) < permutation.get(index + 1)) {
                for (int j = permutation.size() - 1; j != index; j--) {
                    if (permutation.get(j) > permutation.get(index)) {
                        List<Integer> nextPermutation = new ArrayList<>(permutation);
                        Collections.swap(nextPermutation, index, j);

                        final List<Integer> reversed = new ArrayList<>();
                        int start = index + 1;
                        for (int end = nextPermutation.size() - 1; end >= start; end--) {
                            reversed.add(nextPermutation.get(end));
                        }

                        for (int idx = index + 1; idx < nextPermutation.size(); idx++) {
                            nextPermutation.set(idx, reversed.get(idx - index - 1));
                        }

                        return nextPermutation;
                    }
                }
            }
        }
        return new ArrayList<>();
    }
}

