import java.util.ArrayDeque;
import java.util.Deque;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.function.BinaryOperator;

public class RpnEval {
    public static Double evaluateRpn(List<Object> tokens) {
        Map<String, BinaryOperator<Double>> operators = new HashMap<>();
        operators.put("+", (operand1, operand2) -> operand1 + operand2);
        operators.put("-", (operand1, operand2) -> operand1 - operand2);
        operators.put("*", (operand1, operand2) -> operand1 * operand2);
        operators.put("/", (operand1, operand2) -> operand1 / operand2);

        final Deque<Double> stack = new ArrayDeque<>();

        for (Object token : tokens) {
            if (token instanceof Double) {
                stack.push((Double) token);
            } else {
                String operator = (String) token;
                Double operand2 = stack.pop();
                Double operand1 = stack.pop();
                BinaryOperator<Double> operation = operators.get(operator);
                Double result = operation.apply(operand1, operand2);
                stack.push(result);
            }
        }

        return stack.pop();
    }
}

