package java_programs;

import java.util.*;

public class ShortestPathLengths {

    private static final int INF = 99_999;

    public static Map<List<Integer>, Integer> calculateShortestPathLengths(final int numNodes, final Map<List<Integer>, Integer> lengthByEdge) {
        Map<List<Integer>, Integer> lengthByPath = new HashMap<>();

        for (int i = 0; i < numNodes; i++) {
            for (int j = 0; j < numNodes; j++) {
                List<Integer> edge = new ArrayList<>(Arrays.asList(i, j));
                if (i == j) {
                    lengthByPath.put(edge, 0);
                } else if (lengthByEdge.containsKey(edge)) {
                    lengthByPath.put(edge, lengthByEdge.get(edge));
                } else {
                    lengthByPath.put(edge, INF);
                }
            }
        }

        for (int k = 0; k < numNodes; k++) {
            for (int i = 0; i < numNodes; i++) {
                for (int j = 0; j < numNodes; j++) {
                    int updatedLength = Math.min(lengthByPath.get(List.of(i, j)),
                                                     calculateSumLengths(lengthByPath.get(List.of(i, k)), lengthByPath.get(List.of(j, k))));
                    lengthByPath.put(List.of(i, j), updatedLength);
                }
            }
        }

        return lengthByPath;
    }

    private static int calculateSumLengths(final int firstLength, final int secondLength) {
        if (firstLength == INF || secondLength == INF) {
            return INF;
        }
        return firstLength + secondLength;
    }
}

