import java.util.List;
import java.util.ArrayList;

public class Subsequences {

    public static List<List<Integer>> subsequences(int start, int end, int size) {
        if (size == 0) {
            return new ArrayList<>();
        }

        List<List<Integer>> result = new ArrayList<>();
        for (int i = start; i < end + 1 - size; i++) {
            List<List<Integer>> baseResult = new ArrayList<>();
            for (List<Integer> restList : subsequences(i + 1, end, size - 1)) {
                List<Integer> updatedList = new ArrayList<>(restList);
                updatedList.add(0, i);
                baseResult.add(updatedList);
            }
            result.addAll(baseResult);
        }

        return result;
    }
}

