public class WrapUtility {
    public static void main(String[] args) {
        System.out.println("abc".lastIndexOf("c", 30));
    }

    public static List<String> wrapText(final String text, final int cols) {
        List<String> lines = new ArrayList<>();

        String remainingText = text;
        while (remainingText.length() > cols) {
            int end = remainingText.lastIndexOf(' ', cols);
            if (end == -1) {
                end = cols;
            }
            String line = remainingText.substring(0, end);
            remainingText = remainingText.substring(end).trim();
            lines.add(line);
        }

        if (!remainingText.isEmpty()) {
            lines.add(remainingText);
        }

        return lines;
    }
}
